mapper.update:
UPDATE tbl_class

    name = #{name}
    audition_time = #{auditionTime}
    total = #{total}
    sids = #{sids}
    site = #{site}

WHERE id = #{id} AND valid = 1

UPDATE tbl_class
SET valid = 0
WHERE id = #{id}

