mapper.update:
UPDATE tbl_journal
SET valid = 0
WHERE id = #{id}

mapper.sql:
valid = 1

    AND op_id = #{opId}


    AND sid = #{sid}


    AND ctime >= #{ctimeLow}


    AND ctime <= #{ctimeHigh}


    AND cur_stage like '%${curStage}%'


    AND content like '%${content}%'


    AND next_stage like '%${nextStage}%'


    AND next_degree like '%${nextDegree}%'


    AND next_state like '%${nextState}%'


    AND excepted_date >= {exceptedDateLow}


    AND excepted_date <= {exceptedDateHigh}
