name = #{name}, nickname = #{nickname}, age = #{age}, birthday = #{birthday}, parent_name = #{parentName}, phone = #{phone}, alternate_number = #{alternateNumber}, wechat = #{wechat}, addr = #{addr}, source_id = #{sourceId}, state = #{state}, degree = #{degree}, sales_stage = #{salesStage}, expected_date = #{expectedDate}, contract_date = #{contractDate}
