mapper.sql:
valid = 1

    AND name like '%${name}%'


    AND nickname like '%${nickname}%'


    AND parent_name like '%${parentName}%'


    AND phone like '%${phone}%'


    AND alter_number like '%${alternateNumber}%'


    AND wechat like '%${wechat}%'


    AND addr like '%${addr}%'


    AND birthday >= #{birthdayLow}


    AND birthday <= #{birthdayHigh}


    AND expected_date >= #{expectedDateLow}


    AND expected_date <= #{expectedDateHigh}


    AND contract_date >= #{contractDateLow}


    AND contract_date <= #{contractDateHigh}


    AND ctime >= #{ctimeLow}


    AND ctime <= #{ctimeHigh}


    AND age >= #{ageLow}


    AND age <= #{ageHigh}


    AND eid IN

        #{item}



    AND degree IN

        #{item}



    AND sales_stage IN

        #{item}



    AND state IN

        #{item}



